DROP TABLE IF EXISTS [targets];
CREATE TABLE [targets] (

/* @rem so.addVariable('config', '/xml/index?id=1105895-277588-26-0-600') (ptube) */
[ID]			INTEGER PRIMARY KEY AUTOINCREMENT,
/* the name of the task */
[Name]			VARCHAR,
/* the name of the task */
[MimeType]		VARCHAR,
/* the uri being loaded */
[Uri]			VARCHAR,
/* POST|GET */
[UriType]		VARCHAR DEFAULT 'GET',
/* P=PRIMARY|CHILD=NULL */
[TypeName]		VARCHAR,
/* html|xml */
[Format]		VARCHAR,
/* a description of this task */
[Description]	VARCHAR,
/* the next task (or none to end after the task) */
[NextTarget]	VARCHAR,
/* File-Name Regular Expression */
[FRegExp]		VARCHAR,
/*
	INTO|GET
 */
[Action]		INTEGER default 0,
/* string search points */
[ResultFilter]	VARCHAR,
[OffsetStart]	VARCHAR,
[OffsetEnd]		VARCHAR,
[Value]			VARCHAR
);
DROP TABLE IF EXISTS [downloads];
CREATE TABLE [downloads] (
	[ID]			INTEGER PRIMARY KEY AUTOINCREMENT,
	[Name]			VARCHAR,
	[Value]			VARCHAR,
	[Description]	VARCHAR,
	[Exists]		VARCHAR
);
DROP TABLE IF EXISTS [settings];
CREATE TABLE [settings] (
	[ID]			INTEGER PRIMARY KEY AUTOINCREMENT,
	[Name]			VARCHAR,
	[Value]			VARCHAR
);

